import React from 'react';

const Dropdown = props => {    

    const dropdownChanged = e => {
        props.changed(e.target.className);

    }    

    return (
        <div className="col-sm-6 form-group row px-0">     
            <div className={props.selectedValue} onClick={dropdownChanged}>
                {props.options.map((item, idx) => <div key={idx + 1} className={item.id}><img id="dfsf" className={item.id} src={item.icons[0].url}/>{item.name}</div>)}
            </div>            
        </div>
    );
}

export default Dropdown;
