import React from 'react';

const Dropdown = props => {    



    const clicked = e => {
        e.preventDefault();
        props.clicked(e.target.value);
    }   

    return (
        <div className="col-sm-6 form-group row px-0">     
            <button value={props.selectedValue} onClick={clicked} className="">
                {props.options.map((item, idx) => <div key={idx + 1} value={item.id}>{item.name}<img src={item.icons[0].url}/></div>)}
            </button>            
        </div>
    );
}

export default Dropdown;
