import React from 'react';

const Dropdown = props => {    

    const dropdownChanged = e => {
        props.changed(e.target.className);

    }    

    return (
        <div className="">     
            <div className={props.selectedValue} onClick={dropdownChanged}>
                {props.options.map((item, idx) => <div id="" key={idx + 1} className={item.id}>{item.name}</div>)}
                {props.options.map((item, idx) => <img key={idx + 1} id="img__tarck" className={item.id} src={item.icons[0].url}/>)}
            </div>            
        </div>
        
    );
}

export default Dropdown;
