import React from 'react';

const Dropdown = props => {    

    const img = props.option.item.icons[0].url;

    const dropdownChanged = e => {
        props.changed(e.target.value);
        console.log(props);

    }    

    return (
        <div className="col-sm-6 form-group row px-0">     
            <div value={props.selectedValue} onClick={dropdownChanged} className="">
                {props.options.map((item, idx) => <option key={idx + 2} value={item.id}>{item.name}<img src={item.icons[0].url} /></option>)}

            </div>            
        </div>
    );
}

export default Dropdown;
