import React, { useState, useEffect } from 'react';
import Dropdown2 from './Dropdown2';
import Dropdown from './Dropdown';
import Listbox from './Listbox';
import Detail from './Detail';
import { Credentials } from './Credentials';
import axios from 'axios';

const App = () => {


  const spotify = Credentials();  


  const [token, setToken] = useState('');  
  const [genres, setGenres] = useState({selectedGenre: '', listOfGenresFromAPI: []});
  const [playlist, setPlaylist] = useState({selectedPlaylist: '', listOfPlaylistFromAPI: []});
  const [tracks, setTracks] = useState({selectedTrack: '', listOfTracksFromAPI: []});
  const [trackDetail, setTrackDetail] = useState(null);

  useEffect(() => {

    axios('https://accounts.spotify.com/api/token', {
      headers: {
        'Content-Type' : 'application/x-www-form-urlencoded',
        'Authorization' : 'Basic ' + btoa(spotify.ClientId + ':' + spotify.ClientSecret)      
      },
      data: 'grant_type=client_credentials',
      method: 'POST'
    })
    .then(tokenResponse => {      
      setToken(tokenResponse.data.access_token);

      axios('https://api.spotify.com/v1/browse/categories?locale=sv_RU', {
        method: 'GET',
        headers: { 'Authorization' : 'Bearer ' + tokenResponse.data.access_token}
      })
      .then (genreResponse => {        
        setGenres({
          selectedGenre: genres.selectedGenre,
          listOfGenresFromAPI: genreResponse.data.categories.items
        })
      });

    });

  }, [genres.selectedGenre, spotify.ClientId, spotify.ClientSecret]); 

  const genreChanged = val => {
    setGenres({
      selectedGenre: val, 
      listOfGenresFromAPI: genres.listOfGenresFromAPI
    });

    axios(`https://api.spotify.com/v1/browse/categories/${val}/playlists?limit=10`, {
      method: 'GET',
      headers: { 'Authorization' : 'Bearer ' + token}
    })
    .then(playlistResponse => {
      setPlaylist({
        selectedPlaylist: playlist.selectedPlaylist,
        listOfPlaylistFromAPI: playlistResponse.data.playlists.items
      })
    });


  }

  const playlistChanged = val => {

    setPlaylist({
      selectedPlaylist: val,
      listOfPlaylistFromAPI: playlist.listOfPlaylistFromAPI
    });
  }

  const buttonClicked = e => {
    e.preventDefault();

    axios(`https://api.spotify.com/v1/playlists/${playlist.selectedPlaylist}/tracks?limit=10`, {
      method: 'GET',
      headers: {
        'Authorization' : 'Bearer ' + token
      }
    })
    .then(tracksResponse => {
      setTracks({
        selectedTrack: tracks.selectedTrack,
        listOfTracksFromAPI: tracksResponse.data.items
      })
    });
  }

  const listboxClicked = val => {

    const currentTracks = [...tracks.listOfTracksFromAPI];

    const trackInfo = currentTracks.filter(t => t.track.id === val);

    setTrackDetail(trackInfo[0].track);

  }


  return (

    <div className='app'>
        <div className="main__container">
          <div className="main__section">
            <header className="main__header">
              <div className="main__logo">
                <a className="logo__link link__decoration" href="#">
                  <img className="img__logo" src="./Group 1.svg" alt="" />
                  <p className="text__logo">SpotifyStats</p>
                </a>          
              </div>
              <nav className="header__nav">
                <a className="header__link hover__link link__decoration" href="#">Треки</a>
                <a className="header__link hover__link link__decoration" href="#">Исполнители</a>
                <a className="header__link hover__link link__decoration" href="#">Жанры</a>
                <a className="header__link hover__link link__decoration" href="#">История</a>
              </nav>
              <div className="theme__acc">
                <div className="theme__icon">
                  <span id="theme" className="material-symbols-outlined btn-toggle button">
                    bedtime
                  </span>
                  <span id="theme" className="material-symbols-outlined btn-toggle button color__btn hidden">
                    clear_day
                  </span>
                </div>
                <div className="main__account hover__link">Аккаунт</div>
              </div>
            </header>
            <div className="container">
	      	<form onClick={buttonClicked}>
		          <Dropdown label="Genre :" options={genres.listOfGenresFromAPI} selectedValue={genres.selectedGenre} changed={genreChanged} />
		          <Dropdown2 label="Playlist :" options={playlist.listOfPlaylistFromAPI} selectedValue={playlist.selectedPlaylist} changed={playlistChanged} />
		          <div className="col-sm-6 row form-group px-0">
		          </div>
		          <div className="row">
		            <Listbox items={tracks.listOfTracksFromAPI} clicked={listboxClicked} />
		            {trackDetail && <Detail {...trackDetail} /> }
		          </div>        
	      	</form>
	    	</div>                                             
          </div>
        </div>
        <hr className="hr" />
        <footer className="footer">
          <div className="footer__content">© 2022 - SpotifyStats</div>
        </footer>
      </div>


  );
}

export default App;
