import React from 'react';

const Dropdown = props => {    

    const dropdownChanged = e => {
        props.changed(e.target.className);

    }    

    return (
        <section id={props.selectedValue} className="collum" onClick={dropdownChanged}>
        <a className="link__decoration link__track hover__track link__one" href="#">
          <div className="">
            {props.options.map((item, idx) => <div id="" key={idx + 1} className={item.id}><img key={idx + 1} id="img__tarck" className={item.id} src={item.icons[0].url}/>{item.name}</div>)}
          </div> 
        </a>
        </section>
    );
}

export default Dropdown;
