import React from 'react';

const Dropdown2 = props => {    

    const dropdownChanged = e => {
        props.changed(e.target.className);
    }    

    return (
        <section className={props.selectedValue} onClick={dropdownChanged}>
        <a className="link__decoration link__track hover__track link__one" href="#">
          <div className="">
            {props.options.map((item, idx) => <div key={idx + 1} id="content__track" className={item.id}><img  className={item.id} src={item.images[0].url}/>{item.name}</div>)}
          </div> 
        </a>
        </section>
    );
}

export default Dropdown2;
